import Foundation

struct ShuffleAndDeal {
    
    var shuffledDeck = [Int]()
    
    // non-vs matches play with 2 decks of 40 dots (4 colors with 10 numbers per color). vs matches need more dots because there is a continuous feed of them so 4 decks are used. red green blue yellow 0-9 are ordered to form index integer identifier for decks 1,2,3,4. e.g. red number 0 from deck 1 is index identifier "0" and green number 1 from deck 1 is index identifier "11", yellow 9 from deck 1 is index identifier 39, red number 0 from deck 2 is index identifier 40 [index identifier == card, ie card number 65, 101, 5...].
    
    enum DotColor {
        case red
        case green
        case blue
        case yellow
    }
    
    mutating func threeDeckShuffle() {
        var allNumbers = [Int]()
        for i in 0..<84 {
            allNumbers.append(i)
        }
        
        for _ in 0..<67 {
            let randomNumber = Int(arc4random_uniform(UInt32(allNumbers.count)))
            if let index = allNumbers.index(of: allNumbers[randomNumber]) {
                shuffledDeck.append(allNumbers[randomNumber])
                allNumbers.remove(at:index)
            }
        }
    }
    
    mutating func fiveDeckShuffle() {
        var allNumbers = [Int]()
        for i in 0..<140 {
            allNumbers.append(i)
        }
        
        for _ in 0..<140 {
            let randomNumber = Int(arc4random_uniform(UInt32(allNumbers.count)))
            if let index = allNumbers.index(of: allNumbers[randomNumber]) {
                shuffledDeck.append(allNumbers[randomNumber])
                allNumbers.remove(at:index)
            }
        }
    }
    
    func whatColorIsCard(card: Int) -> DotColor {
        var color = DotColor.blue
        switch card {
        case 0...6, 28...34, 56...62, 84...90, 112...118:
            color = .red
            
        case 7...13, 35...41, 63...69, 91...97, 119...125:
            color = .green
            
        case 14...20, 42...48, 70...76, 98...104, 126...132:
            color = .blue
            
        case 21...27, 49...55, 77...83, 105...111, 133...139:
            color = .yellow
            
        default:
            break
        }
        return color
    }
    
    func whatValueIsCard(card: Int) -> Int {
        let value: Int = card%7
        
        return value
    }

    //used in vs matches to feed in next card to the puzzle
    func nextCardAfterThisCard(thisCard: Int) -> Int? {
        var nextCard: Int?
        let next = shuffledDeck.index(of: thisCard)! + 1
        if shuffledDeck.count <= next {
            nextCard = shuffledDeck[next]
        } else {
            nextCard = nil
        }
        return nextCard
    }
 
    
